mapper.select:
select  ao.A_ORDER_NUM, ao.A_ORDER_DATE, ao.A_ORDER_STATUS, ao.A_Delevery_Address, ao.A_DETAIL_ADDRESS, sm.S_MENU_NAME
 from a_order ao left outer join a_order_detail aod on ao.a_order_num = aod.a_order_num
 left outer join s_menu sm on aod.s_menu_code = sm.s_menu_code
 left outer join s_menu_group smg on sm.S_GROUP_NUM = smg.s_group_num


    and ao.A_ORDER_STATUS = #{aOrderStatus} 
    and ao.A_ORDER_NUM = #{aOrderNum}  		  

 and ao.C_ID = #{cId}
 order by ao.a_order_num
