mapper.update:
update S_MENU

		S_MENU_NAME=#{sMenuName},
		S_MENU_INFO=#{sMenuInfo},
		S_MENU_IMG=#{sMenuImg},
		S_MENU_PRICE=#{sMenuPrice}

where S_MENU_CODE=#{sMenuCode}

mapper.select:
select C.S_ID from S_MENU M INNER JOIN s_menu_group G 
ON m.s_group_num= g.s_group_num INNER JOIN s_store S 
ON g.s_store_num=s.s_store_num INNER JOIN S_SELLER C 
ON s.s_store_num=c.s_store_num


	 m.s_menu_code=#{sMenuCode} 
	 and g.s_group_num=#{sGroupNum}
