mapper.update:
update S_MENU

		S_MENU_NAME=#{sMenuName},
		S_MENU_INFO=#{sMenuInfo},
		S_MENU_IMG=#{sMenuImg},
		S_MENU_PRICE=#{sMenuPrice}

where S_MENU_CODE=#{sMenuCode}

mapper.select:
select * from S_SELLER SS left outer join S_STORE ST 
on SS.S_STORE_NUM = ST.S_STORE_NUM LEFT OUTER JOIN S_MENU_GROUP SMG 
ON ST.S_STORE_NUM = SMG.S_STORE_NUM LEFT OUTER JOIN S_MENU SM 
ON SMG.S_GROUP_NUM = SM.S_GROUP_NUM


	 and smg.s_group_num=#{sGroupNum} 
	 and sm.s_menu_code=#{sMenuCode} 
	 and ss.s_store_num=#{sStoreNum} 

and rownum < = 1
